import React, {useState, useEffect} from 'react';
import RepoTable from './RepoTable';
import Typography from '@material-ui/core/Typography';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import Checkbox from '@material-ui/core/Checkbox';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Favorite from '@material-ui/icons/Favorite';
import FavoriteBorder from '@material-ui/icons/FavoriteBorder';
import TablePagination from '@material-ui/core/TablePagination';
import Paper from '@material-ui/core/Paper';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
  table: {
    minWidth: 600,
  },
  h6:{
    fontSize: 14,
  },
  description:{
    fontSize: 14,
    width: 50,
  },
  image: {
    width: 50,
  }
});

interface IDataProps {
    data?: any;
    full_name?: string;
    avatar_url?: string;
    description?: string;
}

function createData(avatar_url: string, full_name: string, description: string) {
  return {avatar_url, full_name, description };
}


function RepoPicker(props: IDataProps) {
  const [checked, setChecked] = useState([]);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [page, setPage] = React.useState(0);
  const classes = useStyles();
  const data =  props.data;
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    window.localStorage.setItem(event.target.id, JSON.stringify(event.target.checked));
    setChecked({...checked, [event.target.id] : event.target.checked });
  }

  useEffect(() => {
    console.log("useEffect",checked)
   }, [checked])


  const handleChangePage = (event: unknown, newPage: number) => {
    setPage(newPage);
  };

  const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  //work around for async call with hooks
  if(data != null && data.items != null) {
    const rows =  data.items.map((item: any) => (
      createData(item.owner.avatar_url, item.full_name, item.description)
    ))
    const emptyRows = rowsPerPage - Math.min(rowsPerPage, rows.length - page * rowsPerPage);
    console.log(data)
    console.log(data.items[0].owner.avatar_url)
    return (
      <div className="App">
        <Typography variant="h4">Repo Picker</Typography>
          <TableContainer component={Paper}>
          <Table size="small" className={classes.table} aria-label="simple table">
            <TableHead>
              <TableRow>
              <TableCell align="center">Avatar</TableCell>
                <TableCell align="center">Name</TableCell>
                <TableCell align="center">Description</TableCell>
                <TableCell align="center"></TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
            {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row: any) => (
              <TableRow key={row.full_name}>
                <TableCell align="center" component="th" scope="row">
                <img alt="avatar" className={classes.image} src={row.avatar_url}/>
                </TableCell>

                <TableCell align="center" component="th" scope="row">
                <Typography className={classes.h6} variant="h6" component="span">
                  {row.full_name}
                  </Typography>
                </TableCell>

                <TableCell align="center" component="th" scope="row">
                  <Typography className={classes.description} variant="h6" component="span">
                    {row.description}
                    </Typography>
                  </TableCell>
                  <TableCell align="center" component="th" scope="row">
                    <Checkbox
                      control={<Checkbox icon={<FavoriteBorder />} checkedIcon={<Favorite />} name="checkedH" />}
                      id={row.full_name}
                      checked={window.localStorage.getItem(row.full_name) == 'true'}
                      onChange={handleChange}
                      inputProps={{ 'aria-label': 'primary checkbox' }}
                    />
                </TableCell>
              </TableRow>
            ))}
            {emptyRows > 0 && (
            <TableRow style={{ height: 48 * emptyRows }}>
              <TableCell colSpan={6} />
            </TableRow>
          )}
          </TableBody>
        </Table>
      </TableContainer>
      <TablePagination
          rowsPerPageOptions={[10]}
          component="div"
          count={rows.length}
          rowsPerPage={rowsPerPage}
          page={page}
          onChangePage={handleChangePage}
          onChangeRowsPerPage={handleChangeRowsPerPage}
        />
      </div>
    );
  }
  return null;
}

export default RepoPicker;
