import React, {useState, useEffect} from 'react';
import Typography from '@material-ui/core/Typography';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import Checkbox from '@material-ui/core/Checkbox';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import TablePagination from '@material-ui/core/TablePagination';
import Paper from '@material-ui/core/Paper';
import { makeStyles } from '@material-ui/core/styles';
import { create } from 'istanbul-reports';

const useStyles = makeStyles({
    table: {
      minWidth: 650,
    },
    h6:{
      fontSize: 14,

    }
  });

  interface IDataProps {
    data?: any;
    full_name?: string;
    avatar_url?: string;
    description?: string;
}

function createData(full_name: string, description: string) {
  return { full_name, description };
}



function RepoTable(props: IDataProps) {
    let initialValue = window.localStorage.getItem('checked' || 'null');
    // const initialValueBool = initialValue
    const [checked, setChecked] = React.useState(initialValue);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const [page, setPage] = React.useState(0);


    useEffect(() => {
      window.localStorage.setItem('checked', JSON.stringify(checked))
    })
    const classes = useStyles();
    console.log(props.data)
    // const data =  props.data;
    // console.log("data: ", data)
    const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
      setChecked(JSON.stringify(event.target.checked));
      console.log(event.target.checked)
    }

    // const handleChecked = (event: React.ChangeEvent<HTMLInputElement>) => {
    //   if (JSON.stringify(event.target.checked) === 'true' ) {
    //     return checked
    //   }
    // }

    const handleChangePage = (event: unknown, newPage: number) => {
      setPage(newPage);
    };

    const handleChangeRowsPerPage = (event: React.ChangeEvent<HTMLInputElement>) => {
      setRowsPerPage(parseInt(event.target.value, 10));
      setPage(0);
    };
    const rows =  data.items.map((item: any) => (
        createData(item.full_name, item.description)
      ))
      const emptyRows = rowsPerPage - Math.min(rowsPerPage, rows.length - page * rowsPerPage);
      console.log(data)
        return (
            <div>
            <Typography variant="h4">Repo Picker</Typography>

            <TableContainer component={Paper}>
            <Table className={classes.table} aria-label="simple table">
              <TableHead>
                <TableRow>
                <TableCell align="left">Avatar</TableCell>
                  <TableCell align="left">Name</TableCell>
                  <TableCell align="left">Description</TableCell>
                  <TableCell align="left">Flag Me</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row: any) => (
                  <TableRow key={row.full_name}>
                    <TableCell component="th" scope="row">
                    <Typography className={classes.h6} variant="h6" component="span">
                      {row.full_name}
                      </Typography>
                    </TableCell>

                    <TableCell component="th" scope="row">
                    <Typography className={classes.h6} variant="h6" component="span">
                      {row.full_name}
                      </Typography>
                    </TableCell>

                    <TableCell align="left">
                      <Typography className={classes.h6} variant="h6" component="span">
                        {row.description}
                        </Typography>
                        </TableCell>
                    <Checkbox
                      key={row.full_name}
                      //  checked={handleChecked}
                      onChange={handleChange}
                      inputProps={{ 'aria-label': 'primary checkbox' }}
                    />
                  </TableRow>
                ))}
                {emptyRows > 0 && (
                <TableRow style={{ height: 48 * emptyRows }}>
                  <TableCell colSpan={6} />
                </TableRow>
              )}
              </TableBody>
            </Table>
          </TableContainer>
          <TablePagination
              rowsPerPageOptions={[10]}
              component="div"
              count={rows.length}
              rowsPerPage={rowsPerPage}
              page={page}
              onChangePage={handleChangePage}
              onChangeRowsPerPage={handleChangeRowsPerPage}
            />
          </div> 
    )
    }

export default RepoTable;
